# Compter le nombre de handle
def how_many
  puts "Combien y a-t-il de handle ?"
  handles = twitter_list
  puts handles.length
  return handles.length
end

# Trouver le handle le plus court
def find_the_lowest
  puts "Quel est le handle le plus court ?"
  tab = twitter_list.sort_by(&:length) # Trie la liste par nombre de caractère (du plus petit au plus grand)
  return puts tab[0] # Récupère le premier élément de la liste (soit le plus petit)
end

# Combien y a-t-il de handle avec 5 caractères ? (sans le @)
def five_char
  puts "Combien y a-t-il de handle avec 5 caractères ? (sans le @)"
  tab = twitter_list
  i = 0
  x = []
  tab.each do
    if tab[i].length == 6
      x.push(tab[i])
    end
    i += 1
  end
  return puts x.length
end

# Combien commencent par une majuscule (première lettre juste après le @) ?
def how_many_upcase
  puts "Combien commencent par une majuscule (première lettre juste après le @)"
  tab = twitter_list
  i = 0
  x = 0
  # Pour chaque éléments de la liste compare la première lettre à elle même transformée en minuscule. Si il y a une différence, alors c'est qu'il y avait une majuscule.
  tab.each do
    if tab[i][1, 1] != tab[i][1, 1].downcase
      x += 1
    end
    i += 1
  end
  return puts x
end

# trier par ordre alphabétique
def sort_the_list
  puts "Trier par ordre alphabétique"
  tab = twitter_list.sort
  return puts tab
end

# Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def sort_the_handles
  puts "Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
  tab = twitter_list
  return puts tab.sort_by(&:length)
end

# Quelle est la position dans l'array de la personne @epenser ?
def epenser_position
  puts "Quelle est la position dans l'array de la personne @epenser ?"
  tab = twitter_list
  i = 0
  x = 0
  tab.each do
    if tab[i] == "@epenser"
      x = tab[i]
      break
    end
    i += 1
  end
  return puts i
end

# Sors-moi une répartition des handles par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def repartition
  puts "Sors-moi une répartition des handles par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
  # créer un hash qui a pour keys : 1, 2, 3, 4 ... et pour values le nombre de handle qui possèdent ces nombre de caractère.
  tab = twitter_list
  rep = {}
  i = 0
  tab.each do
    if rep.has_key?(tab[i].length) == false
      rep.store(tab[i].length, 1)
    else
      rep[tab[i].length] = rep[tab[i].length] + 1
    end
    i += 1
  end
  rep = Hash[rep.sort]
  return puts rep
end


def perform
  how_many
  find_the_lowest
  five_char
  how_many_upcase
  sort_the_list
  sort_the_handles
  epenser_position
  repartition
end

perform
